package ba.coder.wlgpp.day4.HomeWork;

public class OneHundredCount {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 0;
		int sum = 0;
		
		for ( i=1 ; i<=99 ; i++) {
			sum = sum + i;	
		}
		
		System.out.println("1에서 99까지의 합계는 "+ sum+" 입니다");
		
	}

}
